/** @jsxImportSource theme-ui */
import { Button } from 'theme-ui'

type ButtonProps = {
  children: string;
  styles: React.CSSProperties
  handleClick: (event: React.MouseEvent<HTMLButtonElement>) => void
};

// const Button = (props: ButtonProps) => {
//   console.log("Style props: ", props.styles)
//   return <button sx={{backgroundColor:'beigelue', color: 'buttonText'}}
//   onClick={(event) => props.handleClick(event)}>{props.children}</button>;
// };

const ButtonComponent  = (props: ButtonProps) => {
  console.log("Styles: ", props.styles)
  return <Button sx={{backgroundColor:'black', color: 'buttonText'}}
     onClick={(event) => props.handleClick(event)}>{props.children}</Button>
}

export default ButtonComponent;

