import type { Theme } from 'theme-ui'

export const theme: Theme = {
  fonts: {
      header: '"Dm Sans, sans-serif'
  },
  fontWeights:{
      heading: 700,
      button: 500,
      para: 400
  },
  colors: {
      heading: '#151A30',
      para: '#2E3A59',
      text: 'yellow',
      background: 'red'
  },
  buttons: {
      headerBtn: {
          background: 'background',
          color: 'text'
      }
  }
}
