import type { Theme } from 'theme-ui'

export const theme: Theme = {
  fonts: {
      header: '"Dm Sans, sans-serif'
  },
  fontWeights:{
      heading: 500,
      button: 700,
      para: 400
  },
  fontSizes: {
      heading: '2rem',
      para: '1.5rem'
  },
  colors: {
      heading: '#151A30',
      text: '#2E3A59',
      background: '#FFFFFF',
      accent: 'red'
  },
  buttons: {
      headerBtn: {
          background: '#EDF1F7',
          color: '#222B45',
          fontWeight: 'button'
      }
  },
  text: {
      heading: {
          fontWeight: 'heading',
          color: 'heading',
          fontSize: 'heading'
      }
  },

  styles: {
      root: {
          fontFamily: 'header',
          fontSize: '1rem'
      }
  }
}
