import type { Theme } from 'theme-ui'

export const theme: Theme = {
  fonts: {
      header: '"Dm Sans, sans-serif'
  },
  colors: {
      heading: '#151A30',
      para: '#2E3A59',
      buttonText: 'red',
      buttonBackground: 'blue'

  },
  borders:{
      border: '1px solid black',
      borderWidth: '10px',
      borderStyle: 'solid',
      borderColor: ''
  },
  fontSizes: {
      btnText: '2rem'
  }
}
