import type { Theme } from 'theme-ui'

export const theme: Theme = {
  fonts: {
      header: '"Dm Sans, sans-serif'
  },
  fontWeights:{
      heading: 700,
      button: 500,
      para: 400
  },
  colors: {
      heading: '#151A30',
      text: '#2E3A59',
      background: '#FFFFFF'
  },
  buttons: {
      headerBtn: {
          background: '#EDF1F7',
          color: '#222B45',
          fontWeight: 'heading'
      }
  },
}
