export const theme: Theme = {
  fonts: {
      header: '"Dm Sans, sans-serif'
  },
  colors: {
      heading: '#151A30',
      para: '#2E3A59',
      buttonText: '#222B45',
      buttonBackground: '#EDF1F7'
  },
}
