import { fetchPolicies, fetchErrors } from "./PoliciesActions";
import axios from "axios";

const initialState = {
  policies: {},
  error: "",
};

interface Action {
  type: string;
  payload?: any;
}

const policyReducer = {
  name: "policies",

  reducer: (state = initialState, action: Action) => {
    switch (action.type) {
      case "FETCH_POLICIES":
        return {
          policies: action.payload,
          error: "",
        };

      case "FETCH_ERRORS":
        return {
          policies: {},
          error: action.payload,
        };

      default:
        return state;
    }
  },

  selectPolicies: (state: { policies: {} }) => state.policies,

  doFetchPolicies:
    () =>
    ({ dispatch }: any) => {
      axios
        .get("https://jsonplaceholder.typicode.com/users")
        .then((response) => {
          const policies = response.data;
          console.log("Got the data:- ", policies);
          dispatch(fetchPolicies(policies));
        })
        .catch((error) => {
          const errorMsg = error.message;
          console.log("Got the error:- ", errorMsg);
          dispatch(fetchErrors(errorMsg));
        });
    },

  init: (store: { doFetchPolicies: () => void; selectPolicies: () => void }) => {
    store.doFetchPolicies();
    store.selectPolicies();
  },
};

export default policyReducer;
